import { useState } from 'react';

const useDebounce = () => {

    const [debounceState, setDebounceState] = useState({
        value: "",
        delay: 500
    });

    // if value is changed, wait 500 ms before sending the data

    return (
        debounceState
    );
}

export default useDebounce;
