    state = {
        count: 0
      };

    handleIncrement = () => {
    this.setState({ count: this.state.count + 1 });
    };

    return (
    <div className="card-body">
        <p className="card-text">Click Count: {this.state.count}</p>
        <button className="btn btn-primary" onClick={() => setCount(count - 1)}>
            down
        </button>
        <button className="btn btn-primary" onClick={() => setCount(count + 1)}>
            up
        </button>
    </div>
    );
}

export default cardBody;
