import React from 'react';
import "./style.css"
import { Bar } from 'react-chartjs-2';
import { Container, Row, Col } from 'react-bootstrap';

export default function Skills() {

    const state = {
        labels: 
            ['HTML5', 'CSS3', 'JavaScript',
                 'jQuery', 'ReactJS', 'NodeJS', 
                 'AJAX', 'MySQL', 'MongoDB', 'ExpressJS'],
        datasets: [
          {
            label: 'Languages',
            backgroundColor: 'rgba(39,39,131,1)',
            fontColor: "yellow",
            borderColor: 'rgba(0,0,0,1)',
            borderWidth: 0.5,
            data: [90, 85, 80, 80, 75, 70, 90, 70, 70, 80],
            hoverBackgroundColor: "rgb(255, 234, 118)"
          },
        ]
    }

    const state2 = {
        labels: 
            ['User Experience Design', 'User Research', 'Information Architecture', "Interaction Design", 'Visual Design',
                'Information Design', 'Usability Evaluation', 'Usability Testing'],
        datasets: [
          {
            label: 'UX Design',
            backgroundColor: 'rgba(39,39,131,1)',
            borderColor: 'rgba(0,0,0,1)',
            borderWidth: 0.5,
            data: [80, 80, 75, 70, 65, 70, 75, 70],
            hoverBackgroundColor: "rgb(31, 31, 198)"
          },
        ]
    }

    return (
        <div className="skills-section">
            <Container>
                <Row>
                    <Col>
                        <h1>Skills</h1>
                    </Col>
                </Row>
                <Row>
                    <Col xs={12}>
                        <Bar
                            data={state}
                            options={{
                                title:{
                                    display:true,
                                    fontSize:40,
                                },
                                legend:{
                                    display:false,
                                    position:'top'
                                }
                            }}
                        />
                    </Col>
                </Row>
            </Container>
        </div>
    );
}
