import React from 'react';
import FriendCard from './components/FriendCard';
import Wrapper from './components/Wrapper';
import Title from './components/Title';
import friends from './friends.json';

class App extends React.Component {

  state = {
    friendsList: friends
  };

  HandleCrossButtonClicked = (id) => {
    const newList = [];
    for (const friend of this.state.friendsList) {
      if (friend.id !== id) {
        newList.push(friend)
      }
    }
  }

  render() {
    const myFriends = this.state.friendsList.map(friend => {
      return <FriendCard 
        key={friend.id}
        id={friend.id}
        name={friend.name}
        image={friend.image}
        occupation={friend.occupation}
        location={friend.location}
        onClick={this.HandleCrossButtonClicked}
      />;
    })
    return (
      <Wrapper>
        <Title>Friends List</Title>
        {myFriends}
      </Wrapper>
    );
  }
}

export default App;
