import React from "react";
import CardBody from "./cardBody"

function Counter() {

  state = {
    count: 0
  };

  handleIncrement = () => {
      this.setState({ count: this.state.count + 1 });
  };

  handleDecrement = () => {
      this.setState({ count: this.state.count - 1 });
  };


    return (
      <div className="card text-center">
        <div className="card-header bg-primary text-white">
          Click Counter!
        </div>
          {CardBody}
      </div>
    );
}

export default Counter;
