import React from 'react';
import "./style.css"
import {Bar} from 'react-chartjs-2';
import { Container, Row } from 'react-bootstrap';

export default function Skills() {

    const state = {
        labels: 
            ['HTML5', 'CSS3', 'JavaScript',
                 'jQuery', 'ReactJS', 'NodeJS', 
                 'AJAX', 'MySQL', 'MongoDB', 'ExpressJS'],
        datasets: [
          {
            label: 'Languages',
            backgroundColor: 'rgba(39,39,131,1)',
            borderColor: 'rgba(0,0,0,1)',
            borderWidth: 0.5,
            data: [90, 85, 80, 80, 75, 70, 90, 70, 70, 80],
            hoverBackgroundColor: "rgb(255, 234, 118)"
          },
          {
            label: 'UX Design',
            backgroundColor: 'rgba(39,39,131,1)',
            borderColor: 'rgba(0,0,0,1)',
            borderWidth: 0.5,
            data: [90, 85, 80, 80, 75, 70, 90, 70, 70, 80],
            hoverBackgroundColor: "rgb(255, 234, 118)"
          }   
        ]
      }

    return (
        <div className="skills-section">
            <Container className="d-flex justify-content-center">
                <Row>
                    <div className="col-lg-12 flex">
                        <h1>SKILLS</h1>
                        <div className="skills-chart">
                            <Bar
                                data={state}
                                options={{
                                    title:{
                                        display:true,
                                        fontSize:40
                                    },
                                    legend:{
                                    display:true,
                                    position:'right'
                                    }
                                }}
                            />
                        </div>
                    </div>
                </Row>
            </Container>
        </div>
    );
}
